package tech.titans.runwars
import android.content.Context
import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.text.KeyboardActions
import androidx.compose.foundation.text.KeyboardOptions
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.platform.LocalContext
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.text.input.ImeAction
import androidx.compose.ui.text.input.KeyboardType
import androidx.compose.ui.text.input.PasswordVisualTransformation
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import androidx.lifecycle.viewmodel.compose.viewModel
import tech.titans.runwars.views.LoginViewModel

@Composable
fun LoginScreen(navController: androidx.navigation.NavController, viewModel: LoginViewModel = viewModel()) {

    val context = LocalContext.current
    val prefs = context.getSharedPreferences("RunWarsPrefs", Context.MODE_PRIVATE)

    var email by remember { mutableStateOf("") }
    var password by remember { mutableStateOf("") }
    var stayLoggedIn by remember { mutableStateOf(prefs.getBoolean("stayLoggedIn", true)) }
    var errorMessage by remember { mutableStateOf<String?>(null) }
    var isLoading by remember { mutableStateOf(false) }

    // Extract login logic into a function for reuse
    val performLogin: () -> Unit = {
        if(email.isEmpty() || password.isEmpty()){
            errorMessage = "All fields are required"
        } else {
            errorMessage = null
            isLoading = true
            viewModel.login(email, password, { success, error ->
                isLoading = false
                if(success){
                    // Save "stay logged in" preference
                    prefs.edit().putBoolean("stayLoggedIn", stayLoggedIn).apply()
                    navController.navigate("home")
                }
                else{
                    errorMessage = error ?: "Login failed"
                }
            })
        }
    }

    Box(
        modifier = Modifier
            .fillMaxSize()
            .background(Color(0xFF2C1E3C))
            .windowInsetsPadding(WindowInsets.systemBars)
            .padding(24.dp),
        contentAlignment = Alignment.Center
    ) {
        Column(
            horizontalAlignment = Alignment.CenterHorizontally,
            modifier = Modifier.fillMaxWidth()
        ) {

            // Titlul aplicației
            Text(
                text = "RunWars",
                fontSize = 40.sp,
                fontWeight = FontWeight.Bold,
                color = Color.White,
                modifier = Modifier.padding(bottom = 50.dp)
            )

            // Câmp Email
            OutlinedTextField(
                value = email,
                onValueChange = { email = it },
                label = { Text("Email", color = Color(0xFFAAAAAA)) },
                singleLine = true,
                enabled = !isLoading,
                keyboardOptions = KeyboardOptions(
                    keyboardType = KeyboardType.Email,
                    imeAction = ImeAction.Next
                ),
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(bottom = 20.dp),
                colors = OutlinedTextFieldDefaults.colors(
                    focusedBorderColor = Color(0xFF8E5DFF),
                    unfocusedBorderColor = Color(0xFF8E5DFF),
                    cursorColor = Color.White,
                    focusedLabelColor = Color(0xFF8E5DFF),
                    unfocusedLabelColor = Color(0xFFAAAAAA),
                    focusedTextColor = Color.White,
                    unfocusedTextColor = Color.White,
                    disabledBorderColor = Color(0xFF8E5DFF).copy(alpha = 0.5f),
                    disabledLabelColor = Color(0xFFAAAAAA).copy(alpha = 0.5f),
                    disabledTextColor = Color.White.copy(alpha = 0.5f)
                )
            )

            // Câmp Password
            OutlinedTextField(
                value = password,
                onValueChange = { password = it },
                label = { Text("Password", color = Color(0xFFAAAAAA)) },
                singleLine = true,
                enabled = !isLoading,
                visualTransformation = PasswordVisualTransformation(),
                keyboardOptions = KeyboardOptions(
                    keyboardType = KeyboardType.Password,
                    imeAction = ImeAction.Done
                ),
                keyboardActions = KeyboardActions(
                    onDone = { performLogin() }
                ),
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(bottom = 20.dp),
                colors = OutlinedTextFieldDefaults.colors(
                    focusedBorderColor = Color(0xFF8E5DFF),
                    unfocusedBorderColor = Color(0xFF8E5DFF),
                    cursorColor = Color.White,
                    focusedLabelColor = Color(0xFF8E5DFF),
                    unfocusedLabelColor = Color(0xFFAAAAAA),
                    focusedTextColor = Color.White,
                    unfocusedTextColor = Color.White,
                    disabledBorderColor = Color(0xFF8E5DFF).copy(alpha = 0.5f),
                    disabledLabelColor = Color(0xFFAAAAAA).copy(alpha = 0.5f),
                    disabledTextColor = Color.White.copy(alpha = 0.5f)
                )
            )

            // Stay Logged In Checkbox
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(bottom = 30.dp),
                verticalAlignment = Alignment.CenterVertically
            ) {
                Checkbox(
                    checked = stayLoggedIn,
                    onCheckedChange = { stayLoggedIn = it },
                    enabled = !isLoading,
                    colors = CheckboxDefaults.colors(
                        checkedColor = Color(0xFF8E5DFF),
                        uncheckedColor = Color(0xFF8E5DFF),
                        checkmarkColor = Color.White,
                        disabledCheckedColor = Color(0xFF8E5DFF).copy(alpha = 0.5f),
                        disabledUncheckedColor = Color(0xFF8E5DFF).copy(alpha = 0.5f)
                    )
                )
                Spacer(modifier = Modifier.width(8.dp))
                Text(
                    text = "Stay logged in",
                    color = if (isLoading) Color.White.copy(alpha = 0.5f) else Color.White,
                    fontSize = 14.sp
                )
            }

            // Buton Login
            Button(
                onClick = performLogin,
                enabled = !isLoading,
                modifier = Modifier
                    .fillMaxWidth()
                    .height(55.dp),
                colors = ButtonDefaults.buttonColors(
                    containerColor = Color.Black,
                    contentColor = Color.White,
                    disabledContainerColor = Color.Black.copy(alpha = 0.5f),
                    disabledContentColor = Color.White.copy(alpha = 0.5f)
                ),
                shape = MaterialTheme.shapes.medium

            ) {
                if (isLoading) {
                    CircularProgressIndicator(
                        modifier = Modifier.size(24.dp),
                        color = Color.White,
                        strokeWidth = 2.dp
                    )
                } else {
                    Text(
                        text = "Login",
                        fontSize = 18.sp,
                        fontWeight = FontWeight.SemiBold
                    )
                }
            }
            if(!errorMessage.isNullOrEmpty()){
                Spacer(Modifier.height(8.dp))
                Text(text = errorMessage ?: "", color = Color.Red)
            }

            // Text de trecere la Register
            TextButton(
                onClick = { navController.navigate("register") },
                enabled = !isLoading,
                modifier = Modifier.padding(top = 16.dp)
            ) {
                Text(
                    text = "Don't have an account? Register",
                    color = if (isLoading) Color(0xFF8E5DFF).copy(alpha = 0.5f) else Color(0xFF8E5DFF),
                    fontSize = 14.sp
                )
            }
        }
    }
}

